// BRAINSAIT: System health monitoring
// Perform health checks on all critical services

const timestamp = new Date().toISOString();

const healthChecks = [
  {
    name: 'N8N Workflow Engine',
    type: 'internal',
    status: 'healthy',
    responseTime: 5,
    critical: true
  },
  {
    name: 'MASTERLINC Orchestrator',
    type: 'workflow',
    endpoint: '/webhook/masterlinc/orchestrate',
    critical: true
  },
  {
    name: 'TTLINC Translation Service',
    type: 'workflow',
    endpoint: '/webhook/ttlinc/translate',
    critical: true
  },
  {
    name: 'Patient Portal',
    type: 'workflow',
    endpoint: '/webhook/patient-portal/view-appointments',
    critical: false
  }
];

return [
  {
    json: {
      timestamp,
      checks: healthChecks,
      totalChecks: healthChecks.length,
      criticalChecks: healthChecks.filter(c => c.critical).length
    }
  }
];

// BRAINSAIT: Aggregate health check results

const data = $input.item.json;
const timestamp = data.timestamp;
const checks = data.checks;

// Simulate health status
const healthyChecks = checks.map(check => ({
  ...check,
  status: Math.random() > 0.1 ? 'healthy' : 'degraded',
  responseTime: Math.floor(Math.random() * 100) + 10,
  lastChecked: timestamp
}));

const criticalIssues = healthyChecks.filter(c => c.critical && c.status !== 'healthy');
const overallStatus = criticalIssues.length > 0 ? 'degraded' : 'healthy';

const healthReport = {
  timestamp,
  overall: overallStatus,
  summary: {
    totalChecks: healthyChecks.length,
    healthy: healthyChecks.filter(c => c.status === 'healthy').length,
    degraded: healthyChecks.filter(c => c.status === 'degraded').length,
    down: healthyChecks.filter(c => c.status === 'down').length
  },
  checks: healthyChecks,
  criticalIssues: criticalIssues,
  averageResponseTime: Math.floor(
    healthyChecks.reduce((sum, c) => sum + c.responseTime, 0) / healthyChecks.length
  )
};

return { json: healthReport };

